package com.epam.homework.strings.t02;import java.util.Locale;import java.util.ResourceBundle;@SuppressWarnings("WeakerAccess")public class Questions {    private Locale locale=new Locale("ru");    public void setLocal(String lang) {        locale=new Locale(lang);    }    public String getQuestions(int num) {        ResourceBundle bundle = ResourceBundle.getBundle("ListQuestions",locale);        return (bundle.getString("Question" + num) +                " " + bundle.getString("Answer" + num));    }}